// 配置路由的地方
import Vue from 'vue'
import VueRouter from 'vue-router'
// 使用插件
Vue.use(VueRouter)
// 引入路由组件
import Home from '@/pages/Home'
import Login from '@/pages/Login'
import Register from '@/pages/Register'
import Search from '@/pages/Search'
// 配置路由
export default new VueRouter({
    routes:[
        {
            path:'/home',
            component:Home,
            // 在meta上用户可以自定义属性
            meta:{
                show:true
            }
        },
        {
            path:'/login',
            component:Login,
            meta:{
                show:false
            }
        },
        {
            path:'/register',
            component:Register,
            meta:{
                show:false
            }
        },
        {
            name:'search',
            // 如何指定params参数可传递可不传递，在配置路由的时候在占位符后面加个?
            path:'/search/:keyword?',
            component:Search,
            meta:{
                show:true
            },
            // 开启路由传参
            props:true
        },
        // 重定向，在项目跑起来的时候，访问/，立马定向到首页
        {
            path:'*',
            redirect:'/home'
        }
    ]
})
